<?php

require_once("inc/functions.inc.php");
require_once("classes/ArGlyphs.php");

$ArGlyphs = new ArGlyphs();

function get_nodes($father_id)
{
	$return = "";

	$get_sons_query = mysql_query("SELECT id, name FROM member WHERE tribe_id = '1' AND father_id = '$father_id' AND gender = '1'");
	
	if (mysql_num_rows($get_sons_query) > 0)
	{
		while ($son = mysql_fetch_array($get_sons_query))
		{
			$arabic_name = arabic($son["name"]);
			$return .= "'$arabic_name' => array(";
			$return .= get_nodes($son["id"]);
			$return .= "),";
		}
		
	}
	
	return substr($return, 0, strlen($return)-1);
}

function arabic($string)
{
	global $ArGlyphs;
	return $ArGlyphs->convert(iconv("utf-8", "windows-1256", $string));
}

